import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const Logout = () => {
  const [showPopup, setShowPopup] = useState(false); // State for showing popup
  const history = useHistory();

  useEffect(() => {
    const logoutUser = async () => {
      try {
        // Assuming you have a logout API to clear the session
        await axios.post('http://localhost:5000/logout'); // Replace with your backend logout endpoint
        
        // Remove token or session data from localStorage or cookies
        localStorage.removeItem('authToken'); // If you're using localStorage for token storage
        sessionStorage.removeItem('authToken'); // If using sessionStorage
        
        // Show the popup
        setShowPopup(true);
      } catch (error) {
        console.error('Error during logout:', error);
      }
    };

    logoutUser();
  }, []);

  // Handle click event on "OK" button in the popup
  const handleClosePopup = () => {
    setShowPopup(false); // Close the popup
    history.push('/'); // Redirect to the Home page
  };

  return (
    <div>
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h2>Successfully Logged Out</h2>
            <p>You have been logged out successfully!</p>
            <button onClick={handleClosePopup}>OK</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Logout;
