import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Request = () => {
  const { petId } = useParams(); // Get the petId from URL parameters
  const [pet, setPet] = useState(null);
  const [error, setError] = useState(null); // New state to handle errors

  useEffect(() => {
    const fetchPet = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/petlist/${petId}`);
        setPet(response.data); // Set the pet details to state if found
        setError(null); // Reset error if successful
      } catch (error) {
        console.error('Error fetching pet details:', error.message);
        if (error.response && error.response.status === 404) {
          setError('Pet not found'); // Set error message if pet is not found
        } else {
          setError('An error occurred while fetching pet details');
        }
      }
    };

    fetchPet();
  }, [petId]);

  return (
    <div className="request-container">
      {error ? (
        <p>{error}</p> // Display error message if there's an error
      ) : pet ? (
        <>
          <h2>Request {pet.name}</h2>
          <p>Age: {pet.age}</p>
          <p>Type: {pet.type}</p>
          <p>Breed: {pet.breed}</p>
          {pet.image && (
            <img
              src={`http://localhost:5000/uploads/${pet.image.replace(
                'E:\\project\\pet-platform\\pet\\backend\\pet-platform\\pet\\public\\uploads\\',
                ''
              )}`}
              alt={pet.name}
            />
          )}
          
          <div className="request-form">
            <h3>Fill out the form to request {pet.name}:</h3>
            {/* Your request form logic goes here */}
          </div>
        </>
      ) : (
        <p>Loading pet details...</p>
      )}
    </div>
  );
};

export default Request;
