import React, { useState } from 'react';
import axios from 'axios';

const PetForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    type: '',
    breed: '',
    image: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/pets', formData);
      alert(response.data.message);
      setFormData({ name: '', age: '', type: '', breed: '', image: '' });
    } catch (error) {
      alert('Error submitting form');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input name="age" value={formData.age} onChange={handleChange} placeholder="Age" required />
      <input name="type" value={formData.type} onChange={handleChange} placeholder="Type" required />
      <input name="breed" value={formData.breed} onChange={handleChange} placeholder="Breed" required />
      <input name="image" value={formData.image} onChange={handleChange} placeholder="Image URL" required />
      <button type="submit">Submit</button>
    </form>
  );
};

export default PetForm;
