import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';  // Importing the CSS file

const PetForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    type: '',
    breed: '',
  });
  const [imageFile, setImageFile] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [pets, setPets] = useState([]);
  const [categories] = useState(['Dogs', 'Cats', 'Birds']); // Categories for selection

  // Handle form data changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle file input change
  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('name', formData.name);
    data.append('age', formData.age);
    data.append('type', formData.type);
    data.append('breed', formData.breed);
    data.append('image', imageFile);

    try {
      const response = await axios.post('http://localhost:5000/pets', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(response.data.message);
      setFormData({ name: '', age: '', type: '', breed: '' });
      setImageFile(null);
    } catch (error) {
      alert('Error submitting form');
    }
  };

  // Fetch pets based on category and breed
  useEffect(() => {
    const fetchPets = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/pets`, {
          params: { category: selectedCategory, breed: formData.breed, search: searchQuery },
        });
        setPets(response.data.pets);
      } catch (error) {
        console.error('Error fetching pets:', error);
      }
    };

    if (selectedCategory || searchQuery || formData.breed) {
      fetchPets();
    }
  }, [selectedCategory, searchQuery, formData.breed]);

  return (
    <>
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-brand">Pet Adoption</div>
        <div className="navbar-links">
          <a href="/home">Home</a>
          <a href="/petform">PetForm</a>
          <a href="/petlist">PetList</a>
          <a href="/logout">Logout</a>
        </div>
      </nav>

      {/* Pet Form */}
      <div className="pet-form-container">
        <h1>Add a New Pet</h1>
        <form onSubmit={handleSubmit}>
          <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
          <input name="age" value={formData.age} onChange={handleChange} placeholder="Age" required />
          <input name="type" value={formData.type} onChange={handleChange} placeholder="Type" required />
          <input name="breed" value={formData.breed} onChange={handleChange} placeholder="Breed" required />
          <input type="file" onChange={handleFileChange} accept="image/jpeg, image/png" required />
          <button type="submit">Submit</button>
        </form>
      </div>

      {/* Search and Categories Section */}
      <div className="search-category-container">
        <input
          type="text"
          placeholder="Search pets by name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />

        <div className="category-selector">
          <label>Category: </label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">All</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Pet List Display */}
      <div className="pet-container">
        {pets.length > 0 ? (
          pets.map((pet) => (
            <div key={pet._id} className="pet-item">
              <img src={`http://localhost:5000/${pet.image}`} alt={pet.name} className="pet-item-image" />
              <h3>{pet.name}</h3>
              <p>{pet.age} years old</p>
              <p>Breed: {pet.breed}</p>
              <p>Type: {pet.type}</p>
            </div>
          ))
        ) : (
          <p>No pets found.</p>
        )}
      </div>
    </>
  );
};

export default PetForm;
