import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Request = ({ petId }) => {
  const [pet, setPet] = useState(null);

  useEffect(() => {
    const fetchPet = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/petlist/${petId}`);
        setPet(response.data);
      } catch (error) {
        console.error('Error fetching pet details:', error.message);
      }
    };

    fetchPet();
  }, [petId]);

  if (!pet) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{pet.name}</h2>
      <p>Age: {pet.age}</p>
      <p>Type: {pet.type}</p>
      <p>Breed: {pet.breed}</p>
      {pet.image && (
        <img
          src={`http://localhost:5000/uploads/${pet.image.replace('E:\\project\\pet-platform\\pet\\backend\\pet-platform\\pet\\public\\uploads\\', '')}`}
          alt={pet.name}
        />
      )}
    </div>
  );
};

export default Request;
