import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './petList.css'; // Importing the updated CSS file

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedType, setSelectedType] = useState('');

  useEffect(() => {
    // Fetch pets from the backend API
    const fetchPets = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pets');
        setPets(response.data);
      } catch (error) {
        console.error('Error fetching pets:', error);
      }
    };

    fetchPets();
  }, []);

  // Handle search input change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handle type selection change
  const handleTypeChange = (e) => {
    setSelectedType(e.target.value);
  };

  // Filter pets based on search term and selected type
  const filteredPets = pets.filter((pet) => {
    const matchesSearch = pet.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = selectedType ? pet.type === selectedType : true;
    return matchesSearch && matchesType;
  });

  return (
    <div className="pl-pet-list-page">
      {/* Search and Filter Container */}
      <div className="pl-search-filter-container">
        {/* Type Options */}
        <div className="pl-type-options">
          <label htmlFor="type-select">Filter by Type:</label>
          <select id="type-select" value={selectedType} onChange={handleTypeChange}>
            <option value="">All</option>
            <option value="Dog">Dog</option>
            <option value="Cat">Cat</option>
            <option value="Bird">Bird</option>
            {/* Add more options as needed */}
          </select>
        </div>

        {/* Search Bar */}
        <div className="pl-search-bar-container">
          <input
            type="text"
            placeholder="Search by name..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
      </div>

      {/* Pet Grid */}
      <div className="pl-pet-grid">
        {filteredPets.map((pet) => (
          <div key={pet._id} className="pl-pet-item">
            <h3>{pet.name}</h3>
            <p>Type: {pet.type}</p>
            <p>Breed: {pet.breed}</p>
            <p>Age: {pet.age} years</p>
            {pet.image && (
              <img
                src={pet.image} // Ensure this URL is accessible
                alt={pet.name}
                className="pl-pet-item-image"
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PetList;
