// src/components/RequestPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Re.css';

const Request = () => {
  const { id } = useParams(); // Get pet ID from URL
  const [pet, setPet] = useState(null);
  const [formData, setFormData] = useState({ name: '', contact: '', message: '' });
  const [isRequestSent, setIsRequestSent] = useState(false);

  // Fetch pet data based on ID
  useEffect(() => {
    const fetchPet = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/petlist/${id}`);
        setPet(response.data);
      } catch (error) {
        console.error('Error fetching pet details:', error.message);
      }
    };

    fetchPet();
  }, [id]);

  // Handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/request', {
        ...formData,
        petId: id,
      });
      setIsRequestSent(true); // Show confirmation message
    } catch (error) {
      console.error('Error sending request:', error.message);
    }
  };

  return (
    <div className="request-page-container">
      {pet ? (
        <>
          <h2>Request for {pet.name}</h2>
          {pet.image && (
            <img
              className="pet-image"
              src={`http://localhost:5000/uploads/${pet.image.replace('E:\\project\\pet-platform\\pet\\backend\\pet-platform\\pet\\public\\uploads\\', '')}`}
              alt={pet.name}
            />
          )}
          {!isRequestSent ? (
            <form onSubmit={handleSubmit} className="request-form">
              <label>
                Name:
                <input type="text" name="name" value={formData.name} onChange={handleChange} required />
              </label>
              <label>
                Contact:
                <input type="text" name="contact" value={formData.contact} onChange={handleChange} required />
              </label>
              <label>
                Message:
                <textarea name="message" value={formData.message} onChange={handleChange} required />
              </label>
              <button type="submit">Submit Request</button>
            </form>
          ) : (
            <p>Request sent successfully for {pet.name}!</p>
          )}
        </>
      ) : (
        <p>Loading pet details...</p>
      )}
    </div>
  );
};

export default Request;
