import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Request = () => {
  const { id } = useParams(); // Get the _id from URL parameters
  const [pet, setPet] = useState(null);

  useEffect(() => {
    const fetchPet = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/petlist/${id}`);
        setPet(response.data); // Set the pet details to state
      } catch (error) {
        console.error('Error fetching pet details:', error.message);
      }
    };

    fetchPet();
  }, [id]);

  return (
    <div className="request-container">
      {pet ? (
        <>
          <h2>Request {pet.name}</h2>
          <p>Age: {pet.age}</p>
          <p>Type: {pet.type}</p>
          <p>Breed: {pet.breed}</p>
          {pet.image && (
            <img
              src={`http://localhost:5000/uploads/${pet.image.replace('E:\\project\\pet-platform\\pet\\backend\\pet-platform\\pet\\public\\uploads\\', '')}`}
              alt={pet.name}
            />
          )}
          
          <div className="request-form">
            <h3>Fill out the form to request {pet.name}:</h3>
            {/* Your request form logic goes here */}
          </div>
        </>
      ) : (
        <p>Loading pet details...</p>
      )}
    </div>
  );
};

export default Request;
