import React, { useState } from 'react';
import { signInWithEmailAndPassword, GoogleAuthProvider, signInWithPopup } from 'firebase/auth';
import { auth } from './firebase'; // Adjust the import path based on your file structure
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Import the custom CSS for the login page

// Google Authentication provider
const provider = new GoogleAuthProvider();

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [adminUsername, setAdminUsername] = useState('');
  const [adminPassword, setAdminPassword] = useState('');
  const navigate = useNavigate();

  // Static admin credentials
  const adminCredentials = {
    username: 'admin',
    password: 'admin123',
  };

  // Handle email login
  const handleEmailLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      alert('Login successful!');
      navigate('/home'); // Redirect after successful login
    } catch (error) {
      console.error('Login Error:', error);
      alert(error.message);
    }
  };

  // Handle Google sign-in
  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      console.log('User Info:', result.user);
      alert(`Welcome, ${result.user.displayName}`);
      navigate('/home'); // Redirect after successful login
    } catch (error) {
      console.error('Google Sign-In Error:', error);
      alert(error.message);
    }
  };

  // Handle Admin login
  const handleAdminLogin = () => {
    if (adminUsername === adminCredentials.username && adminPassword === adminCredentials.password) {
      alert('Admin login successful!');
      navigate('/home'); // Redirect to home.js for admin
    } else {
      alert('Invalid admin credentials!');
    }
  };

  // Navigate to the registration page
  const goToRegister = () => navigate('/register');

  return (
    <div className="login-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-logo">Snackaroo</div>
        <ul className="navbar-links">
          <li><a href="/">Home</a></li>
          <li><a href="/login">Pet-Form</a></li>
          <li><a href="/login">Pet-List</a></li>
          <li><a href="/login">Login</a></li>
        </ul>
      </nav>

      <div className="login-image-container">
        <img
          src="https://media.istockphoto.com/id/1437973980/vector/adopt-me-poster-with-cute-dog-in-cardboard-box-colorful-vector-banner-template-with-cartoon.jpg?s=612x612&w=0&k=20&c=_CQIMEkDOgjraO0fxbvhJpYvgOUa4x-6MIepEe-9BRs="
          alt="Login Image"
          className="login-image"
        />
      </div>

      <div className="login-form-container">
        <h1 className="login-form-title">
          Log<span style={{ color: 'red' }}>in</span>
        </h1>
        <p className="login-form-subtitle">Enter your details to Login.</p>
        
        {/* Admin login form */}
        <div className="admin-login-container">
          <h2>Admin Login</h2>
          <input
            type="text"
            placeholder="Admin Username"
            className="login-input"
            value={adminUsername}
            onChange={(e) => setAdminUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Admin Password"
            className="login-input"
            value={adminPassword}
            onChange={(e) => setAdminPassword(e.target.value)}
          />
          <button onClick={handleAdminLogin} className="login-button">Admin Log In</button>
        </div>

        {/* Regular user login form */}
        <input
          type="email"
          placeholder="Email"
          className="login-input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          className="login-input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleEmailLogin} className="login-button">Log In</button>
        <p className="forgot-password">Forgot your Password?</p>
        <hr className="login-hr" />
        <button onClick={handleGoogleSignIn} className="google-signin-button">
          Continue with Google
        </button>
        <p>
          Do not have an account?{' '}
          <span onClick={goToRegister} className="register-link">Register</span>
        </p>
      </div>
    </div>
  );
};

export default Login;
