import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Pet.css';

const PetList = () => {
  const [pets, setPets] = useState([]);

  // Fetch pets from the server
  useEffect(() => {
    const fetchPets = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pets');
        setPets(response.data); // Store the fetched pets in state
      } catch (error) {
        console.error('Error fetching pets:', error.message);
      }
    };

    fetchPets();
  }, []);

  return (
    <div className="pet-container">
      <h2>Pet List</h2>
      {pets.map((pet) => (
        <div className="pet-item" key={pet._id}>
          <h3>{pet.name}</h3>
          <p>Age: {pet.age}</p>
          <p>Type: {pet.type}</p>
          <p>Breed: {pet.breed}</p>
          {pet.image && <img className="pet-item-image" src={`http://localhost:5000/${pet.image.replace('pet-platform/pet/public', 'uploads')}`} alt={pet.name} />}

        </div>
      ))}
    </div>
  );
};

export default PetList;
