import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Logout.css';

const Logout = () => {
  const [showConfirmation, setShowConfirmation] = useState(false); // To toggle the confirmation popup
  const navigate = useNavigate();

  // Handle logout click from navbar
  const handleLogoutClick = () => {
    setShowConfirmation(true); // Show confirmation popup when clicking logout
  };

  // Handle "Yes" button click - logout and redirect to home page
  const handleYes = () => {
    // Clear the user authentication data
    localStorage.removeItem('authToken');
    sessionStorage.removeItem('authToken');

    // Redirect to home page after successful logout
    navigate('/');
  };

  // Handle "No" button click - just redirect to home page
  const handleNo = () => {
    setShowConfirmation(false); // Hide confirmation popup
    navigate('/'); // Redirect to home page without logging out
  };

  return (
    <div className="logout-container">
      {/* Show confirmation popup when the logout button is clicked */}
      {showConfirmation ? (
        <div className="confirmation-popup">
          <h2>Do you want to logout from this website?</h2>
          <div className="confirmation-buttons">
            <button onClick={handleYes} className="yes-btn">Yes</button>
            <button onClick={handleNo} className="no-btn">No</button>
          </div>
        </div>
      ) : (
        // The rest of the layout - in case you need to show it somewhere else (navbar, etc)
        null
      )}
    </div>
  );
};

export default Logout;
