import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [showMessage, setShowMessage] = useState(false); // State to show the success message
  const navigate = useNavigate();

  // Function to handle logout
  const handleLogout = () => {
    // Clear authentication data
    localStorage.removeItem('authToken');
    sessionStorage.removeItem('authToken');

    // Show success message
    setShowMessage(true);

    // Set a timeout to redirect to home page after 2 seconds
    setTimeout(() => {
      navigate('/');
    }, 2000); // Redirect after 2 seconds
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">PetAdopt</div>
      <ul className="navbar-links">
        <li><a href="/">Home</a></li>
        <li><a href="/petlist">Pet-List</a></li>
        <li><a href="/recommend">Recommend-Food</a></li>
        <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
      </ul>

      {/* Show logout success message */}
      {showMessage && (
        <div className="logout-message">
          <p>You have logged out successfully!</p>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
