import React, { useState } from 'react';
import axios from 'axios';

const PetForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    type: '',
    breed: '',
  });
  const [imageFile, setImageFile] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Use FormData to handle file uploads
    const data = new FormData();
    data.append('name', formData.name);
    data.append('age', formData.age);
    data.append('type', formData.type);
    data.append('breed', formData.breed);
    data.append('image', imageFile);  // Append the image file

    try {
      const response = await axios.post('http://localhost:5000/pets', data, {
        headers: {
          'Content-Type': 'multipart/form-data', // Set the content type to multipart/form-data
        },
      });
      alert(response.data.message);
      setFormData({ name: '', age: '', type: '', breed: '' });
      setImageFile(null); // Reset the image file input
    } catch (error) {
      alert('Error submitting form');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input name="age" value={formData.age} onChange={handleChange} placeholder="Age" required />
      <input name="type" value={formData.type} onChange={handleChange} placeholder="Type" required />
      <input name="breed" value={formData.breed} onChange={handleChange} placeholder="Breed" required />
      
      <button type="submit">Submit</button>
    </form>
  );
};

export default PetForm;
