import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Pet.css';

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedType, setSelectedType] = useState(''); // For filtering by type (Dogs, Cats, Birds)

  // Fetch pets from the server based on filters (search query and type)
  useEffect(() => {
    const fetchPets = async () => {
      try {
        const response = await axios.get('http://localhost:5000/petlist', {
          params: {
            search: searchQuery,
            type: selectedType, // Send selected type for filtering
          },
        });
        setPets(response.data); // Store the fetched pets in state
      } catch (error) {
        console.error('Error fetching pets:', error.message);
      }
    };

    fetchPets();
  }, [searchQuery, selectedType]); // Re-fetch data when filters change

  return (
    <div className="pet-list-container">
      <h2>Pet List</h2>

      {/* Search and Type Selector */}
      <div className="search-category-container">
        <input
          type="text"
          placeholder="Search pets by name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />

        <div className="type-selector">
          <label>Type: </label>
          <select value={selectedType} onChange={(e) => setSelectedType(e.target.value)}>
            <option value="">All</option>
            <option value="Dog">Dog</option>
            <option value="Cat">Cat</option>
            <option value="Bird">Bird</option>
          </select>
        </div>
      </div>

      {/* Display filtered pets */}
      <div className="pet-container">
        {pets.length > 0 ? (
          pets.map((pet) => (
            <div className="pet-item" key={pet._id}>
              <h3>{pet.name}</h3>
              <p>Age: {pet.age}</p>
              <p>Type: {pet.type}</p>
              <p>Breed: {pet.breed}</p>
              {pet.image && (
                <img
                  className="pet-item-image"
                  src={`http://localhost:5000/${pet.image.replace('E:\\project\\pet-platform\\pet\\backend\\pet-platform\\pet/public/', 'uploads')}`}
                  alt={pet.name}
                />
              )}
            </div>
          ))
        ) : (
          <p>No pets found.</p>
        )}
      </div>
    </div>
  );
};

export default PetList;
