import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
// Import useNavigate instead of useHistory

const Logout = () => {
  const [showMessage, setShowMessage] = useState(false); // To toggle the success message
  const navigate = useNavigate(); // Use useNavigate for navigation

  // Handle logout
  const handleLogout = () => {
    // Clear the user authentication data from localStorage/sessionStorage
    localStorage.removeItem('authToken'); // Remove the authToken from localStorage (or use sessionStorage)
    sessionStorage.removeItem('authToken'); // Remove the authToken from sessionStorage if used

    // Show the success message
    setShowMessage(true);
  };

  // Handle "OK" click, redirect to home page
  const handleRedirect = () => {
    setShowMessage(false);
    navigate('/'); // Redirect to home page after successful logout
  };

  return (
    <div className="logout-container">
      {/* Logout button */}
      {!showMessage ? (
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      ) : (
        // Success message after logout
        <div className="success-message">
          <h2>You have logged out successfully!</h2>
          <button onClick={handleRedirect} className="ok-btn">
            OK
          </button>
        </div>
      )}
    </div>
  );
};

export default Logout;
